@font-face {
    font-family: 'BalooPaaji';
    font-weight: 600;
    src: url('../fonts/BalooPaaji2-SemiBold.ttf');
}

@font-face {
    font-family: 'BalooPaaji';
    font-weight: 400;
    src: url('../fonts/BalooPaaji2-Regular.ttf');
}


$backgroundGrey:#f2f1e6;

$kongresDark: #231f20;
$kongresOrange: #f47826;
$kongresLightBlue: #2384ba;
$kongresBlueForGradient: #37a0cd;

$kongresGradient: linear-gradient(45deg, rgba(35, 132, 186, 1) 0%, rgba(55, 160, 205, 1) 100%);


$fontSizeH1:3.85rem;
$fontSizeH2: 2rem;
$fontSizeH3: 1.625rem;
$fontSizeBodyL: 1.25rem;
$fontSizeBodyM: 1.125rem;
$fontSizeBodyS: 1rem;
$fontSizeBodyXS: 0.8rem;
$fontSizeSmallest: 0.5rem;
$spaceLetters:0.45px;

$bezierFastoutSlowin: cubic-bezier(0.4, 0.0, 0.2, 1);
$cubicBezier:cubic-bezier(.91, .11, .19, .92);
$durationXS: 200ms;
$durationS: 300ms;
$durationM: 400ms;
$durationL: 600ms;
$durationXL: 800ms;

// .container-fluid {
//     max-width: 1700px;
// }

::selection {
    background: $kongresLightBlue;
    color: white;
}

.container-fluid {
    max-width: 1440px;
}

.color-dark {
    color: $kongresDark;
}

.background-dark {
    background: $kongresDark;
}

.color-dark-gradient {
    background: $kongresDark;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    text-shadow: none;
}

.cls-1 {
    fill: #231f20;
}

.cls-2 {
    fill: #2384ba;
