sources.styles/predefinied.scss:
@font-face {
    font-family: 'BalooPaaji';
    font-weight: 700;
    font-display: swap;
    src: url('../fonts/BalooPaaji2-Bold.ttf');
}

@font-face {
    font-family: 'BalooPaaji';
    font-weight: 600;
    font-display: swap;
    src: url('../fonts/BalooPaaji2-SemiBold.ttf');
}

@font-face {
    font-family: 'BalooPaaji';
    font-weight: 400;
    font-display: swap;
    src: url('../fonts/BalooPaaji2-Regular.ttf');
}


$backgroundGrey:#f2f1e6;

$kongresDark: #231f20;
$kongresOrange: #f47826;
$kongresLightBlue: #2384ba;
$kongresBlueForGradient: #37a0cd;
$kongresDarkGrey:#58595B;
$kongresGradient: linear-gradient(45deg, rgba(35, 132, 186, 1) 0%, rgba(55, 160, 205, 1) 100%);


$fontSizeH1:3.85rem;
$fontSizeH2: 2rem;
$fontSizeH3: 1.625rem;
$fontSizeBodyL: 1.25rem;
$fontSizeBodyM: 1.125rem;
$fontSizeBodyS: 1rem;
$fontSizeBodyXS: 0.8rem;
$fontSizeSmallest: 0.5rem;
$spaceLetters:0.45px;

$bezierFastoutSlowin: cubic-bezier(0.4, 0.0, 0.2, 1);
$cubicBezier:cubic-bezier(.91, .11, .19, .92);
$durationXS: 200ms;
$durationS: 300ms;
$durationM: 400ms;
$durationL: 600ms;
$durationXL: 800ms;

// .container-fluid {
//     max-width: 1700px;
// }

::selection {
    background: $kongresLightBlue;
    color: white;
}

.container-fluid {
    max-width: 1440px;
}

.color-dark {
    color: $kongresDark;
}

.color-blue {
    color: $kongresLightBlue;
}

.color-orange {
    color: $kongresOrange;
}

.background-dark {
    background: $kongresDark;
}

.color-dark-gradient {
    background: $kongresDark;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    text-shadow: none;
}

.margin-100-top {
    margin-top: 6.5rem;
}

a {

    &:focus,
    &:active {
        text-decoration: none;
    }
}

.btn.focus,
.btn:focus {
    box-shadow: none;
}

.btn.btn-link:focus {
    box-shadow: none;
}

.btn-success {
    background-color: $kongresOrange;
    border-color: $kongresOrange;

    &.active,
    &.focus,
    &.hover,
    &:hover,
    &:focus,
    &:active {
        background-color: $kongresOrange;
        border-color: $kongresOrange;
    }

    &:not(:disabled):not(.disabled).active:focus,
    &:not(:disabled):not(.disabled):active:focus,
    .show>&.dropdown-toggle:focus {
        box-shadow: 0 0 0 0.2rem #f4782688;
    }
}

.btn-info {
    background-color: $kongresLightBlue;
    border-color: $kongresLightBlue;

    &.active,
    &.focus,
    &.hover,
    &:hover,
    &:focus,
    &:active {
        background-color: $kongresLightBlue;
        border-color: $kongresLightBlue;

    }

    &:not(:disabled):not(.disabled).active:focus,
    &:not(:disabled):not(.disabled):active:focus,
    .show>&.dropdown-toggle:focus {
        box-shadow: 0 0 0 0.2rem #2385ba7c;
    }
}

.page-link {
    color: $kongresLightBlue;
}

.page-item.active .page-link {
    background-color: $kongresLightBlue;
    border-color: $kongresLightBlue;
}

.cls-1 {
    fill: #231f20;
}

.cls-2 {
    fill: #2384ba;
}

.invalid-feedback {
    display: block;
}

.iti {
    width: 100% !important;
}

.columns-2 {
    columns: 2;
}

table a {
    color: $kongresLightBlue;

    &:hover {
        color: $kongresLightBlue;
    }
}

@media (max-width:992px) {
    .columns-2 {
        columns: 1;
    }
}

sources.styles/style.scss:
@import url('aos/dist/aos.css');
@import url('bootstrap/dist/css/bootstrap.min.css');
@import url('owl.carousel/dist/assets/owl.carousel.css');

@import 'predefinied.scss';



body {
    padding: 0;
    margin: 0;
    -webkit-user-drag: none;
    -moz-user-drag: none;
    -ms-user-drag: none;
    -webkit-user-modify: none;
    -moz-user-modify: none;
    -ms-user-modify: none;
    -webkit-tap-highlight-color: transparent;
    -webkit-font-smoothing: antialiased;
    font-weight: 400;
    font-family: 'BalooPaaji', sans-serif;
    font-display: swap;
    color: $kongresDark;
    overflow-x: hidden;
    max-width: 100%;
}


.header {
    position: relative;
    z-index: 10;

    &__logo {
        &-container {
            max-height: 84px;
            display: inline-flex;
            justify-content: space-between;
            align-items: center;
            width: 50%;
            max-width: 450px;

            svg {
                height: 84px;
                width: auto;
            }
        }

        &-sanepid,
        &-kongres {
            display: inline-flex;
        }


        &-kongres {
            svg {
                height: calc(84px - 2.5rem);
            }
        }

        &-text {
            font-size: $fontSizeBodyS;
            font-weight: 600;
            text-transform: uppercase;
            margin-bottom: 0.25rem;
            text-align: center;
        }
    }
}

.subpage {
    &__nav-item {
        display: block;
    }
}

.main {
    margin-top: -140px;
    padding-top: 140px;
    width: 100%;
    overflow-x: hidden;

}

.splash-top-right {

    &-container {
        right: 0;
        position: relative;
    }

    &::before {
        border-radius: 50%;
        position: absolute;
        width: 950px;
        height: 950px;
        background: darken($color: $backgroundGrey, $amount: 3%);
        top: -700px;
        right: -450px;
        display: block;
        content: '';
        display: none;
    }

    &__svg {
        position: absolute;
        top: -700px;
        z-index: -1;
        right: -330px;
        transform: rotate(60deg);
        height: 1300px;
        width: 1300px;

        path {
            fill: darken($color: $backgroundGrey, $amount: 3%);
        }
    }
}

.hero {
    &__heading {
        font-weight: 700;
        font-size: $fontSizeH1;
        color: $kongresLightBlue;
        margin: 0;

        &::selection {
            color: initial;
            background: initial;
        }
    }

    &__illustration {
        max-height: 650px;
        margin-top: -50px;
        margin-right: -100px;

        .dot {
            opacity: 0;
            animation: fading 1.5s alternate-reverse infinite linear;

            &--2 {
                animation-delay: 1.5s;
                animation: fading2 1.5s alternate-reverse infinite linear;
            }


            &--3 {
                animation-delay: 3s;
                animation: fading3 1.5s alternate-reverse infinite linear;
            }
        }

        .gear {
            animation: rotate 4s infinite linear;
            transform-origin: center;
            transform-box: fill-box;

            &--2 {
                animation: rotate 4s infinite reverse linear;
                animation-delay: 0.667s;
            }
        }

        .ray {
            animation: ray .5s infinite alternate-reverse linear;

            &--2 {
                animation: ray2 .5s infinite alternate-reverse linear;
            }
        }

        .clock-part {
            animation: rotate 5s infinite linear;
            transform-origin: 2px 10px;
            transform-box: fill-box;

            &--2 {
                transform-origin: 8px 12px;
                transform-box: fill-box;
                animation: rotate 20s infinite linear;
            }
        }
    }

    &__btn {
        background-color: $kongresOrange;
        border-radius: 100px;
        font-weight: 700;
        border: 2px solid $kongresOrange;
        color: white;
        position: relative;
        display: block;

        span {
            position: relative;
            z-index: 2;
        }

        &-bg {
            position: absolute;
            width: 100%;
            height: 100%;
            background: white;
            left: -100%;
            left: 0;
            // transition: $durationM $cubicBezier;
            transition: $durationM;
            top: 0;
            border-radius: 100px;
            transform: scale(0);
        }

        &:hover {
            color: $kongresOrange;

            .hero__btn-bg {
                left: 0;
                transform: scale(1);
            }
        }
    }

    &__link {
        font-weight: 700;
        color: $kongresLightBlue;
        position: relative;
        overflow: hidden;

        &::before {
            width: 100%;
            content: '';
            height: 2px;
            border-radius: 100px;
            background: $kongresLightBlue;
            bottom: 0;
            left: -100%;
            // left: 0;
            transition: $durationM;
            position: absolute;
        }

        &:hover {
            color: $kongresLightBlue;
            text-decoration: none;

            &::before {
                left: 0;
            }
        }
    }
}

.counters {
    &__item {
        background: $backgroundGrey;
        border-radius: 1rem;
        padding: 1rem;
        text-align: center;
    }

    &__icon-container {
        background: $kongresLightBlue;
        border-radius: 50%;
        display: flex;
        justify-content: center;
        align-items: center;
        width: 70px;
        height: 70px;
        margin: auto;
        position: relative;
        top: -45px;
    }

    &__icon {
        padding: 1rem;
        max-width: 100%;
        max-height: 100%;
    }

    &__text {
        margin-top: -25px;
        font-size: $fontSizeBodyS;
        font-weight: 600;
        text-transform: uppercase;

    }

    &__number {
        font-weight: 700;
        font-size: $fontSizeH3;
        color: $kongresOrange;
    }
}

.about {
    &__heading {
        font-size: $fontSizeH1;
        font-weight: 700;
        margin: 3rem auto;
        color: $kongresOrange;
    }

    &__subheading {

        font-size: $fontSizeBodyL;
        font-weight: 700;
        margin-bottom: 1.5rem;
        color: $kongresLightBlue;
    }

    &__text {
        font-weight: 600;
    }

    &__p {
        &--highlighted {
            font-weight: 600;
            font-size: $fontSizeBodyL;
            color: $kongresLightBlue;
        }
    }

    &__btn {
        background-color: $kongresOrange;
        border-radius: 100px;
        font-weight: 700;
        border: 2px solid $kongresOrange;
        color: white;
        position: relative;
        display: inline-block;

        span {
            position: relative;
            z-index: 2;
        }

        &-bg {
            position: absolute;
            width: 100%;
            height: 100%;
            background: white;
            left: -100%;
            left: 0;
            // transition: $durationM $cubicBezier;
            transition: $durationM;
            top: 0;
            border-radius: 100px;
            transform: scale(0);
        }

        &:hover {
            color: $kongresOrange;

            .hero__btn-bg {
                left: 0;
                transform: scale(1);
            }
        }
    }

    &__social-container {
        background: white;
        // border-radius: 8px;
        // border: 1px solid $kongresOrange;
        padding: 0;
        margin: 0;
    }

    &__social {
        height: 350px;
        border-radius: 16px;
        overflow-y: scroll;

        &-container {
            .nav-item {

                display: inline-flex;
            }
        }

        &-link {
            text-align: center;
            justify-content: center;
            align-items: center;
            width: 100%;
            font-size: $fontSizeBodyL;
            color: $kongresDarkGrey;
            padding: 0.5rem 0;
            transform: translateX(0) translateY(-1px);
            font-weight: 700;

            &:hover {
                text-decoration: none;
                color: $kongresLightBlue;
            }


            // &::before {
            //     content: '';
            //     position: absolute;
            //     top: -3px;
            //     right: -3px;                
            //     border-top-right-radius: 0px;
            //     width: calc(100% + 4px);
            //     height: calc(100% + 4px);

            // }

            // &--facebook::before {
            //     content: '';
            //     position: absolute;
            //     top: -1px;
            //     left: -1px;
            //     border-top: 4px solid white;
            //     border-left: 4px solid white;
            //     border-right: 1px solid $kongresOrange;
            //     border-bottom: 1px solid $kongresOrange;
            //     border-bottom-right-radius: 1rem;
            //     width: calc(100% + 4px);
            //     height: calc(100% + 4px);
            //     opacity: 1;
            //     transition: $durationS;

            // }

            // &--twitter::before {
            //     content: '';
            //     position: absolute;
            //     top: -1px;
            //     right: -1px;
            //     border-top: 4px solid white;
            //     border-left: 1px solid $kongresOrange;
            //     border-right: 4px solid white;
            //     border-bottom: 1px solid $kongresOrange;
            //     border-bottom-left-radius: 1rem;
            //     width: calc(100% + 4px);
            //     height: calc(100% + 4px);
            //     opacity: 1;
            //     transition: $durationS;
            // }



            &.active {
                color: $kongresOrange;


                &::before {
                    // opacity: 0;


                    content: '';
                    position: absolute;
                    bottom: -1px;
                    right: 0;
                    border-bottom: 4px solid #f47826;
                    border-radius: 1rem;
                    width: calc(100% + 4px);
                    height: 0.25rem;
                    opacity: 1;
                    transition: $durationS;
                    background: #f47826;
                }


            }
        }

        .tab-content {
            background: #f1f1f1;
        }

        &::-webkit-scrollbar {
            width: 10px;
            border-radius: 1rem;
        }

        &::-webkit-scrollbar-thumb {
            background: $kongresOrange;
            border-radius: 0.25rem;
        }

        &::-webkit-scrollbar-track {
            background: #f1f1f1;
            border-bottom-left-radius: 0.5rem;
            border-bottom-left-radius: 0.5rem;
        }
    }

}

.how {
    &__heading {
        font-size: $fontSizeH1;
        font-weight: 700;
        margin-bottom: 3rem;
        color: $kongresLightBlue;
    }

    &__number {
        height: 100px;
        width: 100px;
        font-size: $fontSizeH1;
        color: $kongresOrange;
        background: $backgroundGrey;
        border-radius: 50%;
        font-weight: 700;
        margin-bottom: 1rem;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    &__step {
        max-width: 280px;
        display: flex;
        flex-direction: column;
        text-align: center;
        align-items: center;

        &--2 {
            margin-top: 75px;
        }

        &--3 {
            margin-top: 150px;
        }
    }

    &__border-dots {
        border-image-slice: 33% 33%;
        border-style: dotted;
        border-color: $backgroundGrey;
        border-width: 10.8px;
        border-image-repeat: round;
        position: absolute;
        top: -20px;
        left: 20.5%;
        border-top-right-radius: 500px;
        width: 25%;
        height: 261px;
        border-bottom: none;
        border-left: none;
        z-index: -1;
        transform: rotate(-29deg) !important;

        &--2 {
            left: initial;
            right: 21.5%;
            top: calc(42px + 42px + 4px);
            transform: rotate(40deg) scaleY(-1) !important;
            height: 171px;
        }
    }
}

.asking {
    &__lamp {
        top: -80px;
        left: 50px;
        position: absolute;

        .ray {
            animation: ray .25s infinite alternate-reverse linear;

            &--2 {
                animation: ray2 .25s infinite alternate-reverse linear;
            }
        }
    }

    &__decoration {
        position: absolute;
        top: -52px;
        left: -55px;
        transforM: rotate(56deg);
        z-index: 3;

        &--container {
            z-index: 3;
        }

        &--2 {
            position: absolute;
            top: 88px;
            left: -125px;
            transforM: rotate(165deg);
        }
    }

    &__person-container {
        width: 160px;
        position: absolute;
        transform: scaleX(-1);
        left: -70px;

        &::before {
            content: '';
            background-image: url('../images/SVG/splash-bottom.svg');

            position: absolute;
            top: 79px;
            left: -113px;
            width: 740px;
            height: 552px;
            background-repeat: no-repeat;
            background-size: contain;
            z-index: -1;
            transform: rotate(224deg);
        }

    }

    &__heading {
        font-size: $fontSizeH2;
        font-weight: 700;
        margin-bottom: 3rem;
        color: $kongresLightBlue;
    }

    &__subheading {
        font-size: $fontSizeBodyL;
        font-weight: 600;
        margin: 2rem 0;
        color: $kongresOrange;
    }

    &__p {
        font-size: $fontSizeBodyXS;
    }

    &__tip {
        font-weight: 600;
        font-size: $fontSizeH3;
        margin-bottom: 2rem;
        color: $kongresLightBlue;
    }

    &__arrow {
        transform: rotate(270deg) translateX(-7px);
    }

    &__form {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 1rem;
        grid-auto-flow: dense;
    }

    &__input {
        border-radius: 100px;
        padding: 1rem 1.5rem;
        background: $backgroundGrey;
        font-weight: 600;
        border: none;
        outline: none;
        box-shadow: none;

        &:hover {

            border: none;
            outline: none;
            box-shadow: none;
        }
    }


    &__btn {
        background-color: $kongresOrange;
        border-radius: 100px;
        font-weight: 700;
        border: 2px solid $kongresOrange;
        color: white;
        position: relative;
        display: inline-block;
        grid-column-start: 2;
        width: 50%;
        min-width: 150px;
        margin-left: auto;

        span {
            position: relative;
            z-index: 2;
        }

        &-bg {
            position: absolute;
            width: 100%;
            height: 100%;
            background: white;
            left: -100%;
            left: 0;
            // transition: $durationM $cubicBezier;
            transition: $durationM;
            top: 0;
            border-radius: 100px;
            transform: scale(0);
        }

        &:hover {
            color: $kongresOrange;

            .hero__btn-bg {
                left: 0;
                transform: scale(1);
            }
        }
    }


}

