@media (max-width:992px) {

    .header {
        &__logo-container {

            max-width: 400px;
            width: 100%;
        }
    }

    .splash-top-right {
        &::before {
            display: none;
        }

        &__svg {
            top: -230px;
            right: -140px;
            transform: rotate(46deg);
        }
    }



    .hero {
        &__illustration {
            max-height: 260px;
            margin-top: 1rem;
            margin-right: initial;
            margin-bottom: 1rem;
        }
    }

    .how {
        &__steps {
            flex-direction: column;
        }

        &__step {
            width: 100%;
            max-width: 500px;
            margin: auto;
            margin-top: 1rem;
        }

        &__border-dots {
            display: none;
        }
    }

    .asking {
        &__subheading {
            margin: 1rem 0;
        }

        &__heading {
            margin-bottom: 1rem;
        }

        &__input {
            margin-bottom: 0rem;
        }

        &__person-container svg {
            display: none;
        }

        &__person-container::before {
            top: -151px;
            left: -113px;
            width: 780px;
            height: 602px;
        }
    }


    .help {
        &__desktop-borders {
            border: none;
        }

        &__mobile-borders {
            border: none;
            border-top: 1px solid $backgroundGrey;
            border-bottom: 1px solid $backgroundGrey;
        }

        &__container {
            padding: 2rem 2rem 1rem 2rem !important
        }

        &__link {
            font-size: $fontSizeBodyS;
        }


        &__heading {
            width: calc(100% - 8rem);
        }
    }


}

@keyframes fading {
    0% {
        opacity: 0;
    }

    25% {
        opacity: 1;
    }

    50% {
        opacity: 0;
    }

    100% {
        opacity: 0;
    }
}

@keyframes fading2 {
    25% {
        opacity: 0;
    }

    50% {
        opacity: 1;
    }

    75% {
        opacity: 0;
    }


    100% {
        opacity: 0;
    }
}

@keyframes fading3 {
    50% {
        opacity: 0;
    }

    75% {
        opacity: 1;
    }

    100% {
        opacity: 0;
    }
}

@keyframes rotate {
    from {
        transform: rotate(0deg);
    }

    to {
        transform: rotate(360deg);
    }
}

@keyframes ray {
    from {
        transform: scaleX(1) scaleY(1) rotate(0deg);
        opacity: 1;
    }

    to {
        transform: scaleY(1.0022) scaleX(1.0022) rotate(0.1deg);
        opacity: 0.625;
    }
}

@keyframes ray2 {
    from {
        transform: scaleX(1.0032) scaleY(1.0032) rotate(0.1deg);
        opacity: 0.625;

    }

    to {
        transform: scaleX(1) scaleY(1) rotate(0deg);
        opacity: 1;
    }
}



@media (min-width:481px) and (max-width:992px) {
    .splash-top-right__svg {
        top: 60px;
        right: -200px;
        width: 700px;
        transform: rotate(185deg) scaleX(-1);


    }
}

@media (max-width: 480px) {
    .header {
        &__logo-container {
            width: 100%;


        }

        &__logo-container svg {
            height: 64px;
        }

        &__logo-kongres svg {
            height: calc(74px - 2.5rem);
        }
    }

    .hero {
        &__heading {
            font-size: 3.5rem;
        }

        &__illustration {
            max-width: 30vh;
        }
    }

    .asking {

        &__form {
            display: flex;
            flex-direction: column;
        }
    }
}
