// @import url('aos/dist/aos.css');
// @import url('bootstrap/dist/css/bootstrap.min.css');
@import 'predefinied.scss';



body {
    padding: 0;
    margin: 0;
    -webkit-user-drag: none;
    -moz-user-drag: none;
    -ms-user-drag: none;
    -webkit-user-modify: none;
    -moz-user-modify: none;
    -ms-user-modify: none;
    -webkit-tap-highlight-color: transparent;
    -webkit-font-smoothing: antialiased;
    font-weight: 400;
    font-family: 'BalooPaaji';
    color: $kongresDark;
    overflow-x: hidden;
}


.header {
    position: relative;
    z-index: 10;

    &__logo {
        &-container {
            max-height: 84px;
            display: inline-flex;
            justify-content: space-between;
            align-items: center;
            width: 50%;
            max-width: 450px;

            svg {
                height: 84px;
                width: auto;
            }
        }

        &-sanepid,
        &-kongres {
            display: inline-flex;
        }


        &-kongres {
            svg {
                height: calc(84px - 2.5rem);
            }
        }

        &-text {
            font-size: $fontSizeBodyS;
            font-weight: 600;
            text-transform: uppercase;
            margin-bottom: 0.25rem;
            text-align: center;
        }
    }
}

.main {
    overflow-x: hidden;
    margin-top: -140px;
    padding-top: 140px;
    width: 100%;

}

.splash-top-right {

    &-container {
        right: 0;
        position: relative;
    }

    &::before {
        border-radius: 50%;
        position: absolute;
        width: 950px;
        height: 950px;
        background: darken($color: $backgroundGrey, $amount: 3%);
        top: -700px;
        right: -450px;
        display: block;
        content: '';
        display: none;
    }

    &__svg {
        position: absolute;
        top: -700px;
        z-index: -1;
        right: -330px;
        transform: rotate(60deg);
        height: 1300px;
        width: 1300px;

        path {
            fill: darken($color: $backgroundGrey, $amount: 3%);
        }
    }
}

.hero {
    &__heading {
        font-weight: 700;
        font-size: $fontSizeH1;
        color: $kongresLightBlue;
        margin: 0;

        &::selection {
            color: initial;
            background: initial;
        }
    }

    &__illustration {
        max-height: 650px;
        margin-top: -50px;
